import React, {Component, useState} from 'react';
import './App.css';
import TextBox from "./core/textbox";
import {userQuestions} from "./userQuestions";


class App extends Component {
    state = {
        username: '',
        password: '',
        formError: ''

    };
    handleChange = (key) => (value) => {
        this.setState({[key]: value});
    };

    handleSubmit = () => {
        let {username, password} = this.state;
        alert(`${this.state.username}`);
    };

    handleFormError = (data) => {
        this.setState({formError: !!data.error});
        console.log(this.state.formError);
    };

    render() {
        const {username, password} = this.state;
        const question = userQuestions(username, password);

    return (
        <form>
            <TextBox
                question={question.username}
                onChange={this.handleChange(question.username.key)} formError={this.handleFormError}/>
                <br/>

            {/*<TextBox question={question.password} onChange={this.handleChange(question.password.key)}/>*/}
            <button type="button">Submit</button>
        </form>
        );
    }
}

export default App;
export function formQuestions(label: string, value?: string) {
    return {label: label, value: value}
}
