import React, {Component} from 'react';
import './App.css';
import TextBox from "./core/textbox";
import validate, {userQuestions} from "./userQuestions";
import useForm from "./useForm";
import FormError from "./core/form-error";
import Button from "./core/Button";
import Checkbox from "./core/checkbox";
import validateExample from "./validateExample";

const App = () => {
    const submitFunction = async () => {
        console.log(values);
    };

    // call the hook
    const { handleSubmit, handleChange, handleBlur, values, errors } = useForm(validateExample, submitFunction);
        const question = userQuestions(values, errors);

    return (
        <form onSubmit={handleSubmit} noValidate>
            <FormError errors={errors}/>
            <TextBox
                question={question.email}
                onChange={handleChange}
                onBlur={handleBlur}/>
            {errors.email && <p>*{errors.email}</p>}
           <br/>
            <TextBox question={question.password}
                     onChange={handleChange}
                     onBlur={handleBlur}/>
            <Checkbox question={question.accept}
                      onChange={handleChange}
                      onBlur={handleBlur}/>
            <Button/>
        </form>

        );
};

export default App;
