import React, {Component} from 'react';
import './App.css';
import TextBox from "./core/textbox";
import {userQuestions} from "./userQuestions";
import {useSelector} from "react-redux";
import {getFormError} from "./core/form-error.Actions";


class App extends Component {
    state = {
        username: '',
        password: '',
        formError: ''

    };
    handleChange = (key) => (value) => {
        this.setState({[key]: value});
    };

    handleSubmit = () => {
        let {username, password} = this.state;
        alert(`${this.state.username}`)
        this.setState({formError: getFormError});
    };

    render() {
        const {username, password, formError} = this.state;
        const question = userQuestions(username, password);
        console.log(formError);

        return (
            <React.Fragment>
                <form>
                <TextBox
                    question={question.username}
                    onChange={this.handleChange(question.username.key)}/>
                    <br/>

                    <TextBox
                        question={question.password}
                        onChange={this.handleChange(question.password.key)}/>
                    <button type="button" onClick={this.handleSubmit}>Submit</button>
                </form>
            </React.Fragment>
        );
    }

}

export default App;
export function formQuestions(label: string, value?: string) {
    return {label: label, value: value}
}
