import {Validators} from "./library/Validator";
import {isArray} from "redux-actions/lib/utils/isArray";

export const userQuestions = (email, password) => {
    return {
        email: {
            value: email,
            key: 'email',
            label: 'What is your Username',
            min: 5,
            required: {
                validator: Validators.required,
                message: 'Username Must Present'
            },
        },
        password: {
            value: password,
            key: 'password',
            label: 'What is your Password',
            min: 5,
            type: 'password',
            required: passwordValidator(password)
        },
    }
};

const passwordValidator = (password) => password ? {
        validator: Validators.password,
        message: 'Please Add Correct Password'
    } : {
        validator: Validators.required,
        message: 'Please fillout the form'
    };


export const checkValidation = (value) => {
    let isPass = false;
    if (!!value[0] && value[0].length > 0) {
        console.log(!!value && value[0].length > 0);
        isPass = true;
    }
    if (!!value[1] && value[1].length > 0) {
        isPass = true;
    }
    return isPass;
};
