import React, {Component} from 'react';
import './App.css';
import TextBox from "./core/textbox";
import {userQuestions, loginValidate} from "./userQuestions";
import useForm from "./useForm";
import FormError from "./core/form-error";
import Button from "./core/Button";

const App = () => {
        const {values, errors, setErrors, handleChange, handleSubmit} = useForm(loginValidate);
        const onSubmitForm = (event) => {
            handleSubmit(event);
            if(Object.keys(errors).length === 0) {
                console.log(JSON.stringify(values));
            }
        };
        const question = userQuestions(values, errors);
        const addError = (data) => setErrors(data);

    return (
        <form onSubmit={onSubmitForm} noValidate>
            <FormError errors={errors}/>
            <TextBox
                question={question.email}
                onChange={handleChange}
                formError={addError}/>
           <br/>
            <TextBox question={question.password}
                     onChange={handleChange}
                     formError={addError}/>
            <Button disable={!loginValidate(values)}/>
        </form>

        );
};

export default App;
