import React, {Component} from 'react';
import './App.css';
import TextBox from "./core/textbox";
import {userQuestions, validate, onUserFormSummit} from "./userQuestions";
import useForm from "./useForm";
import FormError from "./core/form-error";

const App = () => {
        const {
            values,
            errors,
            setErrors,
            handleChange,
            handleSubmit,
        } = useForm(login, validate);

        function login(event) {
            handleSubmit(event);
            if(Object.keys(errors).length === 0) {
                console.log(JSON.stringify(values));
            }
        }
        const onSubmitForm = (event) => {
            handleSubmit(event);
            if(Object.keys(errors).length === 0) {
                console.log(JSON.stringify(values));
            }
        };
        const question = userQuestions(values.email, values.password);
        const addError = (data) => {
            setErrors(data)
        };

    return (
        <form onSubmit={login} noValidate>
            <FormError error={errors}/>
            <TextBox
                question={question.email}
                onChange={handleChange}
                formError={addError}/>
           <br/>
            <TextBox question={question.password}
                     onChange={handleChange}
                     formError={addError}/>
            <button type="submit" disabled={!validate(values)}>Submit</button>
        </form>

        );
};

export default App;
