import React, {useState} from "react";
const Checkbox = ({question, onChange, onBlur}) => {
    const [values, setValue] = useState({accept: false});
    const handleChange = (accept) => {
        accept.persist();
        setValue({accept});
        onChange(accept)
    };
    let change = true;
    return (
        <div>
            <label>
                <input
                    name={question.key}
                    type="checkbox"
                    value={values[question.key]}
                    defaultChecked={values[question.key] = !change}
                    onChange={handleChange}
                    onBlur={onBlur}/>
                {question.label}
            </label>
            {question.error && <span>{question.error}</span>}
        </div>
    )
};

export default Checkbox;
