import React, {Component} from 'react';
import './App.css';
import TextBox from "./core/textbox";
import validate, {userQuestions} from "./userQuestions";
import useForm from "./useForm";
import FormError from "./core/form-error";
import Button from "./core/Button";
import Checkbox from "./core/checkbox";
import validateExample from "./validateExample";

const App = () => {
    const submitFunction = async () => {
        console.log(values);
    };

    // call the hook
    const { handleSubmit, handleChange, handleBlur, values, errors, setValues } = useForm(validate, submitFunction);
        const question = userQuestions(values, errors);
        const getHandleChange = (event) => {
            setValues(values => ({values, event}))
        };

    return (
        <form onSubmit={handleSubmit} noValidate>
            <FormError errors={errors}/>
            <TextBox
                question={question.email}
                onChange={getHandleChange}
                onBlur={handleBlur}/>
           <br/>
            <TextBox question={question.password}
                     onChange={getHandleChange}
                     onBlur={handleBlur}/>
            {/*<Checkbox question={question.accept}
                      onChange={handleChange}
                      onBlur={handleBlur}/>*/}
        <input name="accept" value={values.accept = false} type="checkbox" onBlur={handleBlur} onChange={handleChange}/>
            <Button/>
        </form>

        );
};

export default App;
