import React, {useState} from "react";

const CheckboxGroupQuestion = ({question, onChange, onBlur}) => {
    const [inputs, setInputs] = useState([]);
    console.log(inputs);

    return (
        <div>
            <p>{question.label}</p>
                {question.data.map(data => (
                    <label key={data.value}>
                        <input
                            type="checkbox"
                            name={question.key}
                            value={data.value}
                            onClick={() => {
                                const index = inputs.findIndex(item => item === data.value);

                                if (~index) {
                                    setInputs([
                                        ...inputs.slice(0, index),
                                        ...inputs.slice(index + 1)
                                    ]);
                                } else {
                                    setInputs([...inputs, data.value]);
                                }
                            }}
                            onBlur={onBlur}
                        />
                        {data.label}
                    </label>
                ))}
        </div>
    );
};

export default CheckboxGroupQuestion;
