import React, {Component, useState} from 'react';
import PropTypes from 'prop-types';
import {getFormError, GotFormErrorAction} from "./form-error.Actions";

import {validateInput} from "../library/Validator";
import {useDispatch} from "react-redux";

const TextBox = ({question, onChange}) => {
    const [error, setError] = useState(false);
    const handleChange = (event) => {
        const {value} = event.target;
        /*const {onChange} = this.props;*/
        onChange(value);
    };

    const handleBlur = (event) => {
        /*const {question} = this.props;*/
        const {value} = event.target;
        const requiredValidators = !!question.required && [
            {check: question.required.validator, message: question.required.message}
        ];
        this.setState({setError: validateInput(requiredValidators, value)})
    };
    return (
        <div>
            {question.label && <label htmlFor="app-input-field">{question.label}</label>}
            <br/>
            <input
                type={!question.type ? 'text' : question.type}
                value={question.value}
                onChange={this.handleChange}
                onBlur={this.handleBlur}
            /><br/>
            {!!error && <span>{error.message}</span>}
        </div>
    )
};

TextBox.propTypes = {
    value: PropTypes.string || PropTypes.number,
    onChange: PropTypes.func.isRequired,
    formError: PropTypes.string
};

TextBox.defaultProps = {
    value: '',
    formError: ''
};

export default TextBox;
