import React, {Component} from 'react';
import './App.css';
import TextboxQuestion from "./core/textbox-question";
import validate, {userQuestions} from "./userQuestions";
import useForm from "./useForm";
import FormErrorSummary from "./core/form-error-summary";
import Button from "./core/Button";
import CheckboxQuestion from "./core/checkbox-question";
import DropdownQuestion from "./core/dropdown-question";
import RadioButtonQuestion from "./core/radioButton-question";

const App = () => {
    const submitFunction = async () => {
        console.log(values);
    };

    // call the hook
    const { handleSubmit, handleChange, handleBlur, values, errors, setValues } = useForm(validate, submitFunction);
        const question = userQuestions(values, errors);

    return (
        <form onSubmit={handleSubmit} noValidate>
            <FormErrorSummary errors={errors}/>
            <TextboxQuestion
                question={question.email}
                onChange={handleChange}
                onBlur={handleBlur}/>
           <br/>
            <TextboxQuestion question={question.password}
                             onChange={handleChange}
                             onBlur={handleBlur}/>
            <CheckboxQuestion question={question.accept}
                      onChange={handleChange}
                      onBlur={handleBlur}/>
            <DropdownQuestion question={question.countries}
                      onChange={handleChange}
                      onBlur={handleBlur}/>
            <RadioButtonQuestion question={question.country}
                      onChange={handleChange}
                      onBlur={handleBlur}/>
        {/*<input name="accept" value={values.accept = false} type="checkbox" onBlur={handleBlur} onChange={handleChange}/>*/}
            <Button/>
        </form>

        );
};

export default App;
