import React, {useState} from 'react';
import PropTypes from 'prop-types';
import {useDispatch} from "react-redux";
import {useSelector} from "react-redux";

import {validateInput} from "../library/Validator";
import {GotFormErrorAction} from "./form-error.Actions";

const TextBox = ({question, onChange, formError}) => {
    const dispatch = useDispatch();
    const selector = useSelector(state => state.formError)
    const [error, setError] = useState({error: false, message: ''});
    const handleChange = (event) => {
        const {value} = event.target;
        onChange(value);
    };

    const sendTrueValue = () => {
        if (!!error && error.message){
            dispatch(GotFormErrorAction(true));
            return <span>{error.message}</span>
        }
    };

    const handleBlur = (event) => {
        const {value} = event.target;
        const requiredValidators = !!question.required && [
            {check: question.required.validator, message: question.required.message}
        ];
        setError(validateInput(requiredValidators, value));
        if(error) {
            formError(error);
        }
        if(!error) {
            formError(error)
        }
    };

    return (
        <div>
            {question.label && <label htmlFor="app-input-field">{question.label}</label>}
            <br/>
            <input
                name={question.key}
                type={!question.type ? 'text' : question.type}
                value={question.value}
                onChange={handleChange}
                onBlur={handleBlur}
            /><br/>
            {sendTrueValue()}
        </div>
    )
};

TextBox.propTypes = {
    value: PropTypes.string || PropTypes.number,
    onChange: PropTypes.func.isRequired,
};

TextBox.defaultProps = {
    value: '',
};

export default TextBox;
