import React, {Component, useState} from 'react';
import './App.css';
import TextBox from "./core/textbox";
import {userQuestions} from "./userQuestions";
import {Validators} from "./library/Validator";
import {validateInput} from "./library/Validator";


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            formError: ''
        };
    }
    handleChange = (key) => (value) => {
        this.setState({[key]: value});
    };

    handleError = (data) => {
        this.setState({formError: !!data ? data : ''})
    };

    handleSubmit = () => {
        let {username, password} = this.state;
        alert(this.handleError = this.handleError.bind(this));
    };

    render() {
        const {username, password} = this.state;
        const question = userQuestions(username, password);

    return (
        <form>
            <TextBox
                question={question.username}
                onChange={this.handleChange(question.username.key)}
                formError={this.handleError}/>
                <br/>

            {/*<TextBox question={question.password} onChange={this.handleChange(question.password.key)}/>*/}
            <button type="button" onClick={this.handleSubmit}>Submit</button>
        </form>
        );
    }
}

export default App;
export function formQuestions(label: string, value?: string) {
    return {label: label, value: value}
}
