import React, {useState} from "react";
const Checkbox = ({question, onChange, onBlur}) => {
    const [values, setValue] = useState({accept: false});
    const handleChange = (event) => {
        let change = false;
        // setValue({accept: change = !change});
        onChange(event)
    };
    const handleClick = () => {
        let change = !change;
        console.log(change);
        setValue({accept: change = !change});
    };
    let change = true;
    return (
        <div>
            <label>
                <input
                    name={question.key}
                    type="checkbox"
                    value={values[question.key]}
                    defaultChecked={values[question.key]}
                    onChange={handleChange}
                    onBlur={onBlur}
                    onClick={handleClick}/>
                {question.label}
            </label>
            {question.error && <span>{question.error}</span>}
        </div>
    )
};

export default Checkbox;
