import React, {Component, useState} from 'react';
import './App.css';
import TextBox from "./core/textbox";
import {userQuestions} from "./userQuestions";
import {Validators} from "./library/Validator";
import {validateInput} from "./library/Validator";


class App extends Component {
    state = {
        username: '',
        password: '',
        formError: ''
    };
    handleChange = (key) => (value) => {
        this.setState({[key]: value});
    };

    handleSubmit = () => {
        let {username, password} = this.state;
        alert(`${this.state.username}`);
    };

    handleError = (data) => {
        if (data.error === true) {
            const requiredValidators = [
                {check: Validators.required, message: 'OH NO'}
            ];
            this.setState({formError: validateInput(requiredValidators, data.error)})
        }
    };

    render() {
        const {username, password, formError} = this.state;
        const question = userQuestions(username, password);

    return (
        <form>
            {formError}
            <TextBox
                question={question.username}
                onChange={this.handleChange(question.username.key)} formError={this.handleError}/>
                <br/>

            {/*<TextBox question={question.password} onChange={this.handleChange(question.password.key)}/>*/}
            <button type="button">Submit</button>
        </form>
        );
    }
}

export default App;
export function formQuestions(label: string, value?: string) {
    return {label: label, value: value}
}
