import React, {useState} from "react";
const Checkbox = ({question, onChange, onBlur}) => {
    const [values, setValue] = useState({});
    const handleChange = (event) => {
        event.persist();
        setValue(values => ({...values, [event.target.name]: event.target.value}));
        onChange(event)
    };
    return (
        <div>
            <label>
                <input
                    name={question.key}
                    type="checkbox"
                    value={question.value}
                    defaultChecked={question.value}
                    onChange={handleChange}
                    onBlur={onBlur}/>
                {question.label}
            </label>
            {question.error && <span>{question.error}</span>}
        </div>
    )
};

export default Checkbox;
