import React from 'react';

const Dropdown = ({question, onChange, onBlur}) => {
    return (
        <div>
            <select
                name={question.key}
                value={question.value}
                onChange={onChange}
                onBlur={onBlur}>
                <option value="">{question.label}</option>
                {question.data.map((item, key) => (
                    <option
                        key={key}
                        value={item.value}>
                        {item.label}
                    </option>
                ))}
            </select>
            {question.error && <span>{question.error}</span>}
        </div>
    )
};

export default Dropdown;
