import React, {Component} from 'react';
import './App.css';
import TextBox from "./core/textbox";
import {userQuestions} from "./userQuestions";


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            formError: ''
        };
    }
    handleChange = (key) => (value) => {
        this.setState({[key]: value});
    };

    handleError = (data) => {
        this.setState({formError: data})
    };

    handleSubmit = (event) => {
        // alert(JSON.stringify(this.state));
        if (event) event.preventDefault();
        alert(setTimeout(x => console.log(this.handleError) , 3000));
    };

    validateForm = (key) => {
        this.setState({[key]: 'fdfdf'})
    };

    render() {
        const {username, password, formError} = this.state;
        let question = userQuestions(username, password);
        console.log(question.username.formError);

    return (
        <form>
            {formError && formError.message ? "Hello World" : ''}
            <TextBox
                question={question.username}
                onChange={this.handleChange(question.username.key)}
                formError={this.handleError}/>
                <br/>

            <TextBox question={question.password} onChange={this.handleChange(question.password.key)} formError={this.handleError}/>
            <button type="button" onClick={this.handleSubmit}>Submit</button>
        </form>
        );
    }
}

export default App;
export function formQuestions(label: string, value?: string) {
    return {label: label, value: value}
}
