import React, {Component} from 'react';
import './App.css';
import TextBox from "./core/textbox";
import {userQuestions} from "./userQuestions";
import useForm from "./useForm";

const App = () => {
        const {
            values,
            errors,
            setErrors,
            handleChange,
            handleSubmit,
        } = useForm(login);

        function login() {
            console.log('No errors, submit callback called!');
        }
        const question = userQuestions(values.email, values.password);
        const addError = (data) => {
            setErrors(state => {
                let list = [];
                list.push(state);
                return {
                    list,
                };
            });
    };

    return (
        <form onSubmit={handleSubmit} noValidate>
            {errors.message && <p>{'Looks like Error has occured, please Check Below Field'}</p>}
            <TextBox
                question={question.email}
                onChange={handleChange}
                formError={addError}/>
           <br/>
            <TextBox question={question.password}
                     onChange={handleChange}
                     formError={addError}/>
            <button type="submit">Submit</button>
        </form>

        );
};

export default App;
