import {Validators} from "./library/Validator";

export const userQuestions = (values) => {
    return {
        email: {
            value: values.email,
            key: 'email',
            label: 'What is your Username',
            min: 5,
            required: {
                validator: Validators.required,
                message: 'Username Must Present'
            },
        },
        password: {
            value: values.password,
            key: 'password',
            label: 'What is your Password',
            min: 5,
            type: 'password',
            required: passwordValidator(values.password)
        },
    }
};

const passwordValidator = (password) => password ? {
        validator: Validators.password,
        message: 'Please Add Correct Password'
    } : {
        validator: Validators.required,
        message: 'Please fillout the form'
    };


export function loginValidate(values) {
    return values.email && values.password ? true : false
};

export default function validate(values) {
    let errors = {};
    const x = !/\S+@\S+\.\S+/.test(values.email);
    if (!values.email) {
        errors.email = 'Email address is required';
    } else if (!/\S+@\S+\.\S+/.test(values.email)) {
        errors.email = 'Email address is invalid';
    }
    if (!values.password) {
        errors.password = 'Password is required';
    } else if (values.password.length < 8) {
        errors.password = 'Password must be 8 or more characters';
    }
    return errors;
};


