import React, {Component} from 'react';
import './App.css';
import TextBox from "./core/textbox";
import validate, {userQuestions, loginValidate} from "./userQuestions";
import useForm from "./useForm";
import FormError from "./core/form-error";
import Button from "./core/Button";

const App = () => {
        const {values, errors, setErrors, handleChange, handleSubmit, handleBlur} = useForm(callback, validate);
        function callback() {
            console.log('No Errors, call back happen');
        }
        const onSubmitForm = (event) => {
            handleSubmit(event);
            if (event.target.checkValidity()) {
                console.log(JSON.stringify(values));
            }
        };
        const question = userQuestions(values);
        const addError = (data) => setErrors(data);

    return (
        <form onSubmit={onSubmitForm} noValidate>
            <FormError errors={errors}/>
            <TextBox
                question={question.email}
                onChange={handleChange}
                formError={addError}/>
           <br/>
            <TextBox question={question.password}
                     onChange={handleChange}
                     formError={addError}/>
            {/*<p>Email</p>
                     <input name={"email"} type="text" value={values.email || ''} onChange={handleChange} onBlur={handleBlur('email')}/>
            <p>{errors.email}</p>
            <p>Password</p>
                     <input name={"password"} type="text" value={values.password || ''} onChange={handleChange} onBlur={handleBlur('password')}/>
            <p>{errors.password}</p>*/}
            <Button/>
        </form>

        );
};

export default App;
