import { createAction } from 'redux-actions';
import createReducer from "../library/createReducer";
import {combineReducers, createStore} from 'redux'


export const GOT_FORM_ERROR = '[APP] [Form Error] Got Error in Form';

export function GotFormErrorAction(error) {
    return { type: GOT_FORM_ERROR, payload: error };
}


const initialState = {
    formError: false
};

function formErrorReducer(state = initialState, action) {
    switch (action.type) {
        case GOT_FORM_ERROR:
            return {...state, formError: action.payload};
        default:
            return state;
    }
}

export let store = createStore(formErrorReducer);
export const getState = store.getState();
export const getFormError = getState.formError;
