import React, {useState} from "react";
const Checkbox = ({question, onChange, onBlur}) => {
    const [values, setValue] = useState({});
    const handleChange = (event) => {
        // event.persist();
        // setValue(values => ({...values, [event.target.name]: event.target.checked}));
        onChange(event.target.checked);
    };
    return (
        <div>
            <label>
                <input
                    name={question.key}
                    type="checkbox"
                    value={question.value || false}
                    defaultChecked={question.value || false}
                    onChange={onChange}
                    onBlur={onBlur}/>
                {question.label}
            </label>
            <p>{question.error && <span>{question.error}</span>}</p>
        </div>
    )
};

export default Checkbox;
