import React, {Component, useState} from "react";
/*const Checkbox = ({question, onChange, onBlur}) => {
    const [values, setValue] = useState({});
    const handleChange = (event) => {
        event.persist();
        setValue(values => ({...values, [event.target.name]: event.target.value}));
        onChange(event)
    };*/
class Checkbox extends Component {
    state = {
        accept: false
    };
    handleChange = (event) => {
        this.setState({accept: event.target.value});
        this.props.onChange(event);
    };

    render() {
        const {question, onBlur} = this.props;
        return (
            <div>
                <label>
                    <input
                        name={question.key}
                        type="checkbox"
                        value={this.state.accept}
                        defaultChecked={this.state.accept}
                        onChange={this.handleChange}
                        onBlur={onBlur}/>
                    {question.label}
                </label>
                {question.error && <span>{question.error}</span>}
            </div>
        );
    }
}

export default Checkbox;
